import HeroImg from "../images/orji.png";
import letChatsIcon from "../images/let'schat.png";
import gitHubIcon from "../images/icons8-github-32.png";
import linkedInIcon from "../images/icons8-linkedin-30.png";
import xIcon from "../images/icons8-twitterx-50.png";

const Hero = () => {
	return (
		<main className="relative flex justify-around items-center w-full h-[82vh] px-28  text-lg text-gray-600 ">
			<div>
				{/* <img
					src={chatBox}
					alt=""
					width={90}
				/> */}
				<h1 className="text-5xl font-bold">
					Orji
					<br />
					Emmanuel
				</h1>
				<span className="font-bold text-2xl">Front-end Developer</span>
				<br />
				<br />
				<p className="w-[370px]">
					I'm an adept front-end developer with great experience in building
					amazing websites/application with modern technologies.
				</p>
				<br />
				<img
					src={letChatsIcon}
					alt=""
					width={80}
				/>
			</div>
			<div>
				<img
					className="rounded-full"
					src={HeroImg}
					alt=""
					width={400}
				/>
			</div>
			<div className="absolute left-10 flex flex-col gap-4">
				<img
					src={linkedInIcon}
					alt=""
					width={25}
				/>
				<img
					src={gitHubIcon}
					alt=""
					width={25}
				/>
				<img
					src={xIcon}
					alt=""
					width={23}
				/>
			</div>
		</main>
	);
};

export default Hero;
