import AboutPage from "./pages/AboutPage/AboutPage";
import HomePage from "./pages/HomePage/HomePage";
import WorksPage from "./pages/WorksPage/WorksPage";

import gitHubIcon from "./images/icons8-github-32.png";
import linkedInIcon from "./images/icons8-linkedin-30.png";
import xIcon from "./images/icons8-twitterx-50.png";

import { BrowserRouter, Routes, Route } from "react-router-dom";

import { ThemeContext } from "./contexts/Theme";
import { useContext } from "react";

import { navLinks } from "./utils/navLinks";

type contactDetail = {
	imgSrc: string;
	content: string;
	contactLink: string;
};

export const contactDetails: contactDetail[] = [
	{
		imgSrc: gitHubIcon,
		content: "Github",
		contactLink: "https://www.github.com/ManuelOrji2",
	},
	{
		imgSrc: linkedInIcon,
		content: "LinkedIn",
		contactLink: "https://www.linkedin.com/in/emmanuel-orji-497365212/",
	},
	{
		imgSrc: xIcon,
		content: "X",
		contactLink: "https:www.twitter.com/_Manuel_Orji",
	},
];

function App() {
	const [{ theme }] = useContext(ThemeContext);

	return (
		<div
			className="flex items-center justify-center h-screen"
			style={{ backgroundColor: theme.backgroundColor, color: theme.color }}
		>
			<BrowserRouter>
				<Routes>
					<Route
						index
						element={<HomePage />}
					/>
					<Route
						path="works"
						element={<WorksPage />}
					/>
					<Route
						path="about"
						element={<AboutPage />}
					/>
				</Routes>
			</BrowserRouter>
			<div className={styles.menuModal}>
				<div className="absolute top-[20%] left-[50%] right-[50%] flex flex-col gap-8 items-center text-center">
					{navLinks.map((navLink, i) => (
						<Link
							className="w-[180px] text-center font-semibold"
							key={i}
							to={navLink.path}
						>
							{navLink.name}
						</Link>
					))}
				</div>
				<div className="absolute top-4 left-4 cursor-pointer">
					<img
						src={hamburgerCloseIcon}
						alt=""
						width={15}
						height={15}
					/>
				</div>
			</div>
		</div>
	);
}

export default App;
