import HeroImg from "../../images/orji.png";
import letChatsIcon from "../../images/let'schat.png";

import hamburgerCloseIcon from "../../images/X HamburgerMenu.png";

import { contactDetails } from "../../App";
import { navLinks } from "../../utils/navLinks";

import styles from "../Hero/Hero.module.scss";
import { Link } from "react-router-dom";

const Hero = () => {
	return (
		<main
			className={`${styles.hero} relative  w-full h-[90%] px-28 text-lg text-gray-600`}
		>
			<div className="flex justify-around items-center">
				<div className="relative">
					<h1 className="text-5xl font-semibold mb-2">
						Orji
						<br className={styles.br} />
						<span> </span>Emmanuel .I
					</h1>
					<span className="font-semibold text-2xl">FRONT-END DEVELOPER</span>
					<br />
					<p className="w-[370px] mt-4">
						I'm an adept front-end developer with great experience in building
						amazing websites/applications with modern technologies.
					</p>
					<br />
					<div className={`${styles.heroBtn} absolute h-20 w-20 right-[58%]`}>
						<a
							href="https://www.linkedin.com/in/emmanuel-orji-497365212/"
							rel="noopener noreferrer"
							target="_blank"
						>
							<img
								className={`${styles.letsChat}  cursor-pointer`}
								src={letChatsIcon}
								alt=""
								width={80}
							/>
						</a>
					</div>
				</div>
				<div className={styles.heroImg}>
					<img
						className="rounded-full"
						src={HeroImg}
						alt=""
						width={350}
					/>
				</div>
			</div>

			<div className="absolute top-56 left-10 flex flex-col gap-8">
				{contactDetails.map((detail, i) => (
					<a
						href={detail.contactLink}
						key={i}
						target="_blank"
						rel="noopener noreferrer"
					>
						<img
							src={detail.imgSrc}
							alt=""
							width={25}
							className="cursor-pointer"
							title={detail.content}
						/>
					</a>
				))}
			</div>
			<div className={styles.menuModal}>
				<div className="absolute top-[20%] left-[50%] right-[50%] flex flex-col gap-8 items-center text-center">
					{navLinks.map((navLink, i) => (
						<Link
							className="w-[180px] text-center font-semibold"
							key={i}
							to={navLink.path}
						>
							{navLink.name}
						</Link>
					))}
				</div>
				<div className="absolute top-4 left-4 cursor-pointer">
					<img
						src={hamburgerCloseIcon}
						alt=""
						width={15}
						height={15}
					/>
				</div>
			</div>
		</main>
	);
};

export default Hero;
