import Logo from "../../images/plogo.png";
import resumeIcon from "../../images/icons8-resume-50.png";
import worksIcon from "../../images/icons8-document-30.png";
import aboutIcon from "../../images/icons8-contact-50.png";
import mailIcon from "../../images/icons8-mail-50.png";
import lightNdarkModeIcon from "../../images/icons8-dark-mode-50.png";

import styles from "./Nav.module.scss";
import { Link, useLocation } from "react-router-dom";

import { ThemeContext } from "../../contexts/Theme";
import { useContext } from "react";

type navLinkDetails = {
	imgSrc: string;
	path: string;
	name: string;
};

const navLinks: navLinkDetails[] = [
	{
		imgSrc: worksIcon,
		path: "/works",
		name: "Works",
	},
	{
		imgSrc: aboutIcon,
		path: "/about",
		name: "About",
	},
];

const Nav = () => {
	const [{ theme, isDark }, toggleTheme] = useContext(ThemeContext);
	const location = useLocation();

	return (
		<nav className="w-full px-7 py-5 text-gray-600 flex items-center justify-between">
			<div className="flex items-center justify-between w-1/3">
				<Link
					to="/"
					className="pl-4 cursor-pointer"
				>
					<img
						src={Logo}
						alt=""
						width={30}
					/>
				</Link>
				<div
					className={`flex gap-1 items-center font-semibold cursor-pointer ${styles.mailLink}`}
				>
					<img
						src={mailIcon}
						alt=""
						width={20}
						height={20}
					/>
					<a
						href="mailto:nuelorji98@gmail.com"
						rel="noopener noreferrer"
						target="_blank"
					>
						nuelorji98@gmail.com
					</a>
				</div>
				<div
					className="cursor-pointer"
					onClick={toggleTheme}
					title={isDark ? "Switch to Light" : "Switch to Dark"}
				>
					<img
						src={lightNdarkModeIcon}
						alt=""
						width={25}
						height={25}
					/>
				</div>
			</div>

			<div className="w-1/3 flex justify-evenly">
				{navLinks.map((navLink, i) => (
					<Link
						to={navLink.path}
						className={
							location.pathname === navLink.path
								? `flex gap-1 underline items-center cursor-pointer ${styles.navlink}`
								: `flex gap-1 items-center cursor-pointer ${styles.navlink}`
						}
					>
						<img
							src={navLink.imgSrc}
							alt=""
							width={20}
							height={20}
						/>
						<span>{navLink.name}</span>
					</Link>
				))}
				<div>
					<img
						src={navLinks[2].imgSrc}
						alt=""
					/>
					<span>{navLinks[2].name}</span>
				</div>
			</div>
		</nav>
	);
};

export default Nav;
