import HeroImg from "../../images/orji.png";
import letChatsIcon from "../../images/let'schat.png";
import gitHubIcon from "../../images/icons8-github-32.png";
import linkedInIcon from "../../images/icons8-linkedin-30.png";
import xIcon from "../../images/icons8-twitterx-50.png";

import styles from "../Hero/Hero.module.scss";

const Hero = () => {
	return (
		<main className="relative flex justify-around items-center w-full h-[82vh] px-28  text-lg text-gray-600 ">
			<div>
				<h1 className="text-5xl font-bold mb-2">
					Orji
					<br />
					Emmanuel
				</h1>
				<span className="font-bold text-xl">FRONT-END DEVELOPER</span>
				<br />
				<br />
				<p className="w-[370px]">
					I'm an adept front-end developer with great experience in building
					amazing websites/applications with modern technologies.
				</p>
				<br />
				<div className="h-20">
					<img
						className={`${styles.letsChat} ml-20 cursor-pointer`}
						src={letChatsIcon}
						alt=""
						width={80}
					/>
				</div>
			</div>
			<div>
				<img
					className="rounded-full"
					src={HeroImg}
					alt=""
					width={350}
				/>
			</div>
			<div className="absolute left-10 flex flex-col gap-4">
				<img
					src={linkedInIcon}
					alt=""
					width={25}
					className="cursor-pointer"
					title="LinkedIn"
				/>
				<img
					src={gitHubIcon}
					alt=""
					width={25}
					className="cursor-pointer"
					title="Github"
				/>
				<img
					src={xIcon}
					alt=""
					width={23}
					className="cursor-pointer"
					title="X"
				/>
			</div>
		</main>
	);
};

export default Hero;
